update.foreach:
UPDATE pms_project_task
SET  sort_num = #{item.sortNum},
p_id = #{item.pId}
WHERE id = #{item.id}

mapper.select:
select t1.id, t1.task_name, t1.task_type, t1.task_status, t1.start_date, t1.end_date, t1.completion_date, t1.description, t1.assign_user_id, t1.predecessor_id, t1.duration, t1.sort_num, t1.p_id, t1.project_id, t1.progress, t2.nick_name as assignUserName, t4.user_id, t4.nick_name from pms_project_task t1 left join sys_user t2 on t1.assign_user_id = t2.user_id left join pms_project_task_collaborator t3 on t1.id = t3.task_id left join sys_user t4 on t3.user_id = t4.user_id where 1 = 1
and t1.project_id = #{projectId}
and t1.id = #{taskId}
order by t1.sort_num
